// import UserInput from "../component/UserInput";
// import {useNavigate} from "react-router-dom";
import React, {useContext, createContext} from "react";
// import "./Login.css";
// import {isLoginContext, userDataContext, setLoginContext} from "../App";
// import Logo from "../component/Logo";
import { Alert, Button, Form, Row, Col, Stack } from "react-bootstrap";
import { AuthContext } from "../context/AuthContext";

export const inputContext = createContext(null);
export const inputRefContext = createContext(null);

const Login = () => {
    const {
        loginUser,
        loginError,
        loginInfo,
        updateLoginInfo,
        isLoginLoading,
    } = useContext(AuthContext) || {};

    const inputValue = {
        inputID: loginInfo.email, // inputID는 loginInfo.email로 대체
        setID: (email) => updateLoginInfo({ ...loginInfo, email }), // setID는 updateLoginInfo로 대체
        inputPW: loginInfo.password, // inputPW는 loginInfo.password로 대체
        setPW: (password) => updateLoginInfo({ ...loginInfo, password }), // setPW는 updateLoginInfo로 대체
    };

    const inputRefs = {
        inputIDRef: React.createRef(),
        inputPWRef: React.createRef(),
    };

    return (
        <inputContext.Provider value={{inputValue}}>
            <inputRefContext.Provider value={{inputRefs}}>
                <Form onSubmit ={loginUser}>
                    <Row style={{
                        height: "100vh",
                        justifyContent: "Center",
                        paddingTop: "10%"
                    }}>
                        <Col xs={6}>
                            <Stack gap={3}>
                                <h2>Login</h2>

                                <Form.Control type="email" placeholder="Email" onChange={(e) => updateLoginInfo
                                    ({ ...loginInfo, email: e.target.value })} />
                                <Form.Control type="password" placeholder="Password" onChange={(e) => updateLoginInfo
                                    ({ ...loginInfo, password: e.target.value })}/>

                                <Button variant="primary" type="submit" >
                                    {isLoginLoading? "Getting you in ... " : "Login"}
                                </Button>
                                {loginError?.error && 
                                <Alert variant="danger">
                                    <p>{loginError?.message}</p></Alert>} 
                            </Stack>
                        </Col>
                    </Row>
                </Form>
            </inputRefContext.Provider>
        </inputContext.Provider>
    );
};

export default Login;
