import UserInput from "../component/UserInput";
import {useNavigate} from "react-router-dom";
import React, {useRef, useState, useContext, createContext} from "react";
import {isLoginContext, userDataContext, setLoginContext} from "../App";
import Logo from "../component/Logo";
import { Alert, Button, Form, Row, Col, Stack } from "react-bootstrap";
import { AuthContext } from "../context/AuthContext";

export const inputContext = createContext(null);
export const inputRefContext = createContext(null);

const Login = () => {
    const {
        loginUser,
        loginError,
        loginInfo,
        updateLoginInfo,
        isLoginLoading,
    } = useContext(AuthContext);

    // 상태와 Ref 초기화
    const [inputID, setID] = useState(""); // 입력받은 아이디를 저장하는 변수
    const [inputPW, setPW] = useState(""); // 입력받은 비밀번호를 저장하는 변수
    const inputIDRef = useRef();
    const inputPWRef = useRef();

    return (
        <inputContext.Provider value={{ inputID, setID, inputPW, setPW }}>
            <inputRefContext.Provider value={{ inputIDRef, inputPWRef }}>
                <Form onSubmit={loginUser}>
                    <Row style={{
                        height: "100vh",
                        justifyContent: "Center",
                        paddingTop: "10%"
                    }}>
                        <Col xs={6}>
                            <Stack gap={3}>
                                <h2>Login</h2>

                                <UserInput /> {/* UserInput 컴포넌트 사용 */}

                                <Button variant="primary" type="submit">
                                    {isLoginLoading ? "Getting you in ..." : "Login"}
                                </Button>
                                {loginError?.error && 
                                <Alert variant="danger">
                                    <p>{loginError?.message}</p></Alert>} 
                            </Stack>
                        </Col>
                    </Row>
                </Form>
            </inputRefContext.Provider>
        </inputContext.Provider>
    );
};

export default Login;
